INSERT INTO `razmetka` (`id`, `html`) VALUES
(1, '<div class="left_b">\r\n<h1 class="left_h1">Главная</h1>\r\n<img src="img/r1.png" border="0" width="180" height="188" />\r\n</div>\r\n'),
(2, '<div class="left_b">\r\n<h1 class="left_h1">Товары</h1>\r\n<img src="img/r2.png" border="0" width="180" height="135" />\r\n</div>\r\n'),
(3, '<div class="left_b">\r\n<h1 class="left_h1">Заказать</h1>\r\n<img src="img/r3.png" border="0" width="180" height="180" />\r\n</div>\r\n'),
(4, '<div class="left_b">\r\n<h1 class="left_h1">Контакты</h1>\r\n<img src="img/r4.png" border="0" width="180" height="180" />\r\n</div>\r\n'),
(5, '<div id="right_b">\r\n<h1>Работаем по<br/> всему Миру!</h1>\r\n<br/>\r\n<!--Flash-->\r\n<div style="width: 150px; height: 150px; background: #009933 url(''img/shar.png'') left top no-repeat;">\r\n            <object classid="clsid:d27cdb6e-ae6d-11cf-96b8-444553540000" codebase="http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=10,0,0,0"\r\n            width="150" height="150" id="shar" align="middle" >\r\n            <param name="allowScriptAccess" value="sameDomain" />\r\n            <param name="allowFullScreen" value="false" />\r\n            <param name="movie" value="shar.swf"/>\r\n            <param name="quality" value="high" />\r\n            <param name="bgcolor" value="#009933" />\r\n            <param name="menu" value="false" />\r\n            <param name="play" value="true" />\r\n            <param name="loop" value="false" />\r\n            <param name="wmode" value="opaque" />\r\n            <embed src="shar.swf" quality="high" bgcolor="#FFFFFF" width="150" height="150" wmode="opaque"\r\n            name="shar" align="middle" allowscriptaccess="sameDomain" allowfullscreen="false"\r\n            type="application/x-shockwave-flash" pluginspage="http://www.adobe.com/go/getflashplayer_ru" />\r\n            </object>                                    \r\n</div>\r\n</div>'),
(6, '<h1 class="h1_opisan">Добро пожаловать!</h1>\r\n<br/>\r\n<div class="div_opisan">\r\n&nbsp;&nbsp;&nbsp;Наша компания работает по всему миру - мы имеем представительства во всех\r\nстранах Мира! Длительный опыт работы и команда высококвалифицированных специалистов\r\nгарантируют высокое качество наших услуг.\r\n</div>\r\n<div class="div_opisan">\r\n&nbsp;&nbsp;&nbsp;Проект "Виннипух и компания" был создан командой энтузиастов своего дела.\r\nОсновными задачами проекта являются распространение продуктов пчеловодства\r\nи пропаганда здорового образа жизни. Продукты пчеловодства - это сокровища,\r\nкоторые подарила нам природа для поддержания активного образа жизни!\r\n</div>\r\n<div class="div_opisan">\r\n&nbsp;&nbsp;&nbsp;Проект "Виннипух и компания" является некоммерческим, благотворительным.\r\nФинансирование проекта происходит за счет основной деятельности компании -\r\nлечебной деятельности.\r\n</div>\r\n<div class="div_opisan">\r\n&nbsp;&nbsp;&nbsp;Предлагаемые продукты пчеловодства можно получить бесплатно! При формировании\r\nзаказа имеется ограничение по количеству отгружаемого товара: не более 1% от \r\nколичества на складе. Полученные товары запрещается использовать в коммерческих \r\nцелях!\r\n</div>\r\n<br/>'),
(7, '<h1 class="h1_opisan">Главное представительство</h1>\r\n<br/>\r\n<div class="div_opisan">\r\n+7 (495) 000-00-00<br/>\r\n+7 (499) 000-00-00<br/>\r\n<br/>\r\nАдрес: Москва, проспект Виннипуха, дом 7<br/>\r\n<br/>\r\nE-mail: puh@zprog.ru<br/>\r\n<br/> \r\n</div>'),
(8, '<h1 class="h1_opisan">Правила заказа товаров</h1>\r\n<br>\r\n<div class="div_opisan">\r\nПриобрести необходимые товары можно бесплатно!\r\nПри формировании заказа имеется ограничение по количеству \r\nотгружаемого товара: не более 1% от количества на складе.\r\nТекущие остатки товаров представлены в таблице ниже.\r\nПолученные товары запрещается использовать в коммерческих целях!\r\nДля оформления заказа необходимо указать ваше имя и телефон.\r\nПосле этого наши менеджеры позвонят вам для уточнения информации\r\nпо заказу.<br/><br/>\r\n</div>'),
(9, '<div id="zakaz">\r\n<form action="index.php?id=2" target="_self" method="post">\r\n<table border="1" cellpadding="0" cellspacing="0" width="520">\r\n<tr>\r\n<th>Товар</th><th>Остаток, кг</th>\r\n</tr>\r\n'),
(10, '<tr><td>-----</td><td>-----</td></tr>        \r\n<tr><td>Имя (*):</td><td><input type="text" name="user_name" maxlength="15" /></td></tr>\r\n<tr><td>Телефон (*):</td><td><input type="text" name="user_tel" maxlength="20"/></td></tr>\r\n<tr><td></td><td><input type="submit" name="btn" value="Заказать звонок"/></td></tr>\r\n</table>\r\n</form>\r\n<br/>\r\n</div>\r\n<br/>');

